module Wasp.Generator.Common
  ( ProjectRootDir,
    nodeVersion,
    nodeVersionBounds,
    npmVersionBounds,
    prismaVersionBounds,
  )
where

import Wasp.SemanticVersion (SemanticVersion (..), VersionBounds (..))

-- | Directory where the whole web app project (client, server, ...) is generated.
data ProjectRootDir

-- | Node version that node packages generated by this generator expect.
nodeVersion :: SemanticVersion
nodeVersion = SemanticVersion 16 0 0 -- Latest LTS version.

nodeVersionBounds :: VersionBounds
nodeVersionBounds = CompatibleWith nodeVersion

npmVersion :: SemanticVersion
npmVersion = SemanticVersion 8 0 0 -- Latest LTS version.

npmVersionBounds :: VersionBounds
npmVersionBounds = CompatibleWith npmVersion

prismaVersion :: SemanticVersion
prismaVersion = SemanticVersion 3 9 1

prismaVersionBounds :: VersionBounds
prismaVersionBounds = Exact prismaVersion
